class Solution {
private:
    void reverse(ListNode * & head){
        ListNode * curr = head;
        ListNode * prev = nullptr;
        ListNode * next = nullptr;
        while(curr){
            next=curr->next;
            curr->next=prev;
            prev=curr;
            curr=next;
        }
        head=prev;
    }
    void insertHead(int num,ListNode * & ans){
        ListNode * nodetoInsert = new  ListNode(num);
        if(ans==nullptr){
            ans = nodetoInsert;
            return;
        }
        else{
            nodetoInsert->next=ans;
            ans=nodetoInsert;
            return;
        }
