    ListNode* addTwoNumbers(ListNode* l1, ListNode* l2) {
        ListNode* a = reverseList(l1);
        ListNode* b = reverseList(l2);

        ListNode *answer = nullptr, *curr = nullptr;

        int carry = 0;
        while(a && b) {
            int val = a->val + b->val + carry;
            carry = val / 10;
            val = val % 10;
            ListNode* newNode = new ListNode(val);

            if(!answer) {
                answer = newNode;
                curr = newNode;
