        int len1=getCount(l1);
        int len2=getCount(l2);
        // System.out.println(len1+";;;"+len2);
        BigInteger num1 = new BigInteger("0");
        BigInteger num2 = new BigInteger("0");
        BigInteger res = new BigInteger("0");
        len1--;
        len2--;
        while(l1!=null)
        {
            res=BigInteger.valueOf(l1.val);
            l1=l1.next;
            num1=(res.multiply(BigInteger.valueOf((int)Math.pow(10,len1)))).add(num1);
            len1--;
        }
        while(l2!=null)
        {
            res=BigInteger.valueOf(l2.val);
            l2=l2.next;
            num2=(res.multiply(BigInteger.valueOf((int)Math.pow(10,len2)))).add(num2);
            len2--;
        }
        BigInteger num3=num1.add(num2);
        //System.out.println(num3);
        String s=String.valueOf(num3);
        ListNode list= new ListNode(0);
        ListNode temp=list;
        for(int i=0;i<s.length();i++)
        {
            int digit=Integer.parseInt(s.charAt(i)+"");
            ListNode newNode = new ListNode(digit);
            temp.next = newNode;
            temp=newNode;
           // System.out.println(digit+";;;"+temp.val);
        }
        return list.next;
    }

    //count length of linked list
    public int getCount(ListNode head)
    {
        ListNode temp = head;
        int count = 0;
        while (temp != null) {
            count++;
            temp = temp.next;
        }
        return count;
    }
}

