import collections

class Solution:
    def groupAnagrams(self, strs: List[str]) -> List[List[str]]:

        ans = collections.defaultdict(list)

        for string in strs:

            count = [0] * 26

            for character in string:

                count[ord(character) - ord("a")] += 1

            ans[tuple(count)].append(string)

        return ans.values()
