class Solution {
public:
    int maxPoints(vector<vector<int>>& points) {
        int maxi = 0;
        // x -> x[0],x[1]  (x1,y1)
        for (auto& x : points) {
            map<double, int> slopes;
            // y -> y[0],y[1]  (x2,y2)
            for (auto& y : points) {
                if (x == y) continue;
                double slope = numeric_limits<double>::infinity();
                // if (x2 - x1 != 0)
                if (y[0] - x[0] != 0) {
                    // slope = (y2 - y1) / (x2 - x1)
                    slope = (double)(y[1] - x[1]) / (y[0] - x[0]);
                }
                ++slopes[slope];
                maxi = max(maxi, slopes[slope]);
            }
        }
        return maxi + 1;
    }
};
