            for(int j=i+1; j<points.size(); j++){
                if((points[i][0] - points[j][0]) != 0){
                    float m = (float(points[i][1] - points[j][1]) / float(points[i][0] - points[j][0]));
                    map[m]++; 
                    if(map[m]>max_points)
                        max_points = map[m];
                }
                else{
                    map[INT_MAX]++;
                    if(map[INT_MAX]>max_points)
                        max_points = map[INT_MAX];
                }
            }
        }

        return max_points+1;
    }
};
