class Solution {

    struct LineType: Hashable, Equatable {

        var x: Int = 0
        var y: Int = 0

        var hashValue : Int {
          get {
              return x.hashValue ^ y.hashValue
          }
        }

        //Note: this doesn't appear to be necessary.
        static func == (lhs: LineType, rhs: LineType) -> Bool {
            return lhs.x == rhs.x && lhs.y == rhs.y
        }
    }

    func maxPoints(_ points: [[Int]]) -> Int {

        var result = 0

        for i in 0..<points.count {

            var j = i - 1

            var horCount = 0
            var verCount = 0
            var sameCount = 0
            var slopeCount = 0

            var slopeMap = [LineType: Int]()

            while j >= 0 {

                var dx = points[i][0] - points[j][0]
                var dy = points[i][1] - points[j][1]

                if dx == 0 && dy == 0 {
                    sameCount += 1
                } else if dx == 0 {
                    verCount += 1
                } else if dy == 0 {
                    horCount += 1
                } else {

                    var gcd = 0
                    var num1 = dx
                    var num2 = dy
                    var remainder = 0
                    while num1 != 0 {
                        remainder = num2 % num1
                        num2 = num1
                        num1 = remainder
                    }
                    gcd = num2

                    var slopeClassification = LineType(x: dx / gcd, y: dy / gcd)

                    if var count = slopeMap[slopeClassification] {
                        count += 1
                        slopeMap[slopeClassification] = count
                    } else {
                        slopeMap[slopeClassification] = 1
                    }

                    if var count = slopeMap[slopeClassification] {
                        slopeCount = max(slopeCount, count)
                    }
                }
                j -= 1
            }

            var checkResult = max(max(horCount, verCount), slopeCount)
            result = max(result, checkResult + sameCount + 1)
        }
        return result
    }
}
```

