class Solution {
public:
    int maxPoints(vector<vector<int>>& points) { 
        int n = points.size();

        if(n <= 2) return n;

        int ans = 2; //we will fix two point and then check about other points to be on their line or not by slop concept
        for(int i = 0; i < n; i++){
            for(int j = i+1; j < n; j++){
                int curr = 2; //two point are fixed so
                for(int k = 0; k < n; k++){
                    if(k != i and k != j){ //i and j are fixed points so
                        int x1 = points[i][0];
                        int y1 = points[i][1];
                        int x2 = points[j][0];
                        int y2 = points[j][1];
                        int x3 = points[k][0];
                        int y3 = points[k][1];
                        if((x2-x1)*(y3-y1) == (y2-y1)*(x3-x1)) curr++;
                    }
                }
                ans = max(ans, curr);
            }
        }
        return ans;
    }
};
```

