/**
 * Definition for a point.
 * class Point {
 *     int x;
 *     int y;
 *     Point() { x = 0; y = 0; }
 *     Point(int a, int b) { x = a; y = b; }
 * }
 */
public class Solution {
    public int maxPoints(Point[] points) {
        if(points == null) return 0;
        int n = points.length;
        if(n <= 2) return n;
        Map<Long, Integer> map = new HashMap<>();
        int result = 0;
        for(int i = 0; i < n - 1; ++i){
            int localMax = 0, overlap = 0;
            map.clear();
            for(int j = i + 1; j < n; ++j){
                if(points[i].x == points[j].x && points[i].y == points[j].y){
                    overlap++;
                }else{`enter code here`
                    long k = slope(points[i].x - points[j].x, points[i].y - points[j].y);
                    int t = map.containsKey(k) ? map.get(k) + 1 : 1;
                    map.put(k, t);
                    localMax = t > localMax ? t : localMax;
                }
            }
            localMax += overlap + 1;
            result = result < localMax ? localMax : result;
        }
        return result;
    }
    long slope(int a, int b){
        int c = gcd(a, b);
        if(c != 0){
            a /= c;
            b /= c;
        }
        long r = a;
        r = (r << 32) | b;
        return r;
    }
    int gcd(int a, int b){
        int r;
        while(b != 0){
            r = a % b;
            a = b;
            b = r;
        }
        return a;
    }
}

