         return std::abs(lhs - rhs) < 
                std::numeric_limits<float>::epsilon();
    }

    bool belongs(line l, point p) const {
        std::array<float, 3> v{distance(l.begin, p), distance(l.begin, l.end), distance(l.end, p)};
        std::sort(begin(v), end(v));
        return equals(v.back(), v.front() + v[1]);
    }

    int maxPoints(vector<vector<int>>& points) {
        if(points.empty()) return 0;
        if(points.size() == 1) return 1;
        std::vector<std::pair<line, int>> lines;
        for(int i = 0; i < points.size(); i++){
            for(int j = i + 1; j < points.size(); j++){
                point p1{points[i][0], points[i][1]};
                point p2{points[j][0], points[j][1]};
                lines.push_back({{p1, p2}, 0});
            }
        }

        for(auto& e : lines){
            for(int i = 0; i < points.size(); i++){
                point p{points[i][0], points[i][1]};
                if(belongs(e.first, p)){
                    e.second++;
                }
            }
        }

        auto max_el = std::max_element(begin(lines), end(lines), [](auto lhs, auto rhs){
            return lhs.second < rhs.second;
        });

        return max_el->second;
    }
};`

