class Solution {
public:
    vector<int> intersection(vector<int>& nums1, vector<int>& nums2) {

        //create a resultant vector RES and a set to store all the diffrent itersection points in both arrays
        vector<int> res;
        set<int> ans;



        for(int i=0;i<nums1.size();i++){
            //iterate over first array and skip if any iteration is repeated.(we do need to return diffrent/unique elements)
            if( i>0 && nums1[i]==nums1[i-1])
                continue;
            //when element is unique, check for the same element in second array.. if found, insert it into the set
            for(int j=0;j<nums2.size();j++){
                if(nums1[i]==nums2[j]){
                    ans.insert(nums1[i]);
                     break;
                }

            }
        }
        //assing all the values of set into vector (as we need to return a vector)
        res.assign(ans.begin(),ans.end());
        //return resultant vector
        return res;
    }
};
