class Solution
{
    public int[] intersection(int[] A, int[] B)
	{
        Set<Integer> set = new HashSet<>();
        Arrays.sort(A);
        Arrays.sort(B);
        int i = 0;
        int j = 0;

		while(i < A.length && j < B.length)
		{
            if(A[i] == B[j])
			{
                set.add(A[i]);
                i++;
                j++;
            }
			else if (A[i] < B[j])
			{
                i++;
            }
			else
			{
                j++;
            }
        }
		int[] result = new int[set.size()];
        int k = 0;

		for(int n : set)
		{
            result[k++] = n;
        }
        return result;
    }
}
