// #2 Method to find the intersection elements from two given arrays - O(NLogN + MLogM) & O(N)
vector<int> intersection(vector<int>& nums1, vector<int>& nums2) { 
    set<int> st;
    vector<int> ans;

    // Requires to easily access Kth element at last
    sort(begin(nums1), end(nums1));
    sort(begin(nums2), end(nums2));

    int i = 0, j = 0;

    while(i < nums1.size() && j < nums2.size()) {
        if(nums1[i] < nums2[j]) // If ith element is lesser
            i++; // Than move the ith one as its not possible to have the ith element again

        else if(nums1[i] > nums2[j]) // If jth element is lesser
            j++; // Than move the jth one as its not possible to have the jth element again 

        else
            st.insert(nums1[i]), // Both the elements are equal hence store it to set
            i++, j++; // Move forward in both the array
    }

    for(auto val: st)
        ans.push_back(val);

    return ans;
}
