        /*First Pushing all the elements of the first array into the hash set*/
        for (int i = 0; i < nums1.size(); i++)
        {
            hash.insert (nums1[i]);
        }


        vector <int> inter;             
        /*Vector to store all the interesecting elements (which may contain dupilcate values as well.)*/

        /*Here we shall check all the elements of second array whether it already exists in the hash set.
        If it does, that means it is an intersecting element and hence we push it into the inter vector*/
        for (int i = 0; i < nums2.size(); i++)
        {
            if (hash.count (nums2[i]) <= 0)
            {
                continue;
            }
            else
            {
                inter.push_back (nums2[i]);
