class Solution {
public:
	vector<int> intersection(vector<int>& nums1, vector<int>& nums2) {
		unordered_map<int,int>m,m2;
		vector<int>ans;
		for(auto &i:nums1)
			m[i]++;
		for(auto &i:nums2)
