class Solution {
    public int[] intersection(int[] nums1, int[] nums2) {
        boolean[] check = new boolean[1001]; // 0 <= nums1[i], nums2[i] <= 1000(given)
        int[] res = new int[Math.max(nums1.length,nums2.length)];

        for(int num: nums1)
            check[num] = true;

        int count = 0;
        for(int num: nums2){
            if(check[num] == true){
                res[count] = num;
                count++;
                check[num] = false;
            }
        }

        return Arrays.copyOf(res,count);
    }
}
