class Solution:
    def binarySearch(self, nums, n, target):
        start = 0
        end = n - 1
        while start <= end:
            mid = start + ((end - start) // 2)
            if nums[mid] == target:
                return True
            elif nums[mid] < target:
                start = mid + 1
            else:
                end = mid -1
        return False

    def intersection(self, nums1: List[int], nums2: List[int]) -> List[int]:
        n1 = len(nums1)
        n2 = len(nums2)


        if n1 < n2:
            n1, n2 = n2, n1
            nums1, nums2 = nums2, nums1
        nums2.sort()

        output = set()
        for num in nums1:
            if self.binarySearch(nums2, n2, num):
                output.add(num)

        return output
