class Solution(object):
    def intersection(self, nums1, nums2):
        """
        :type nums1: List[int]
        :type nums2: List[int]
        :rtype: List[int]
        """
        nums1 = sorted(nums1)
        nums2 = sorted(nums2)
        i, j = 0, 0
        rst = []

        # Follow-up: only record the last pair
        while i < len(nums1) and j < len(nums2):
            if nums1[i] == nums2[j]:
                while i + 1< len(nums1) and nums1[i] == nums1[i + 1]:
                    i += 1
                while j + 1< len(nums2) and nums2[j] == nums2[j + 1]:
                    j += 1  
                rst.append(nums1[i])
                i += 1
                j += 1
            elif nums1[i] > nums2[j]:
                j += 1
            else:
                i += 1
        return rst

        # Follow-up: only record the first pair
        while i < len(nums1) and j < len(nums2):
            if nums1[i] == nums2[j]:
                if (i == 0 and j == 0) or (i == 0 and nums2[j] != nums2[j - 1]) or (nums1[i] != nums1[i - 1] and j == 0) or (nums1[i] != nums1[i - 1] and nums2[j] != nums2[j - 1]):
                    rst.append(nums1[i])
                i += 1
                j += 1
            elif nums1[i] > nums2[j]:
                j += 1
            else:
                i += 1
        return rst

        # Original: 2 sets
        if len(nums1) < len(nums2):
            return self.intersection(nums2, nums1)
        rst, set2 = set(), set(nums2)
        for num in nums1:
            if num in set2:
                rst.add(num)
        return rst
