class Codec:
    def serialize(self, root):
        if root == None:
            return "$"
        return str(root.val) + "," + self.serialize(root.left) + "," + self.serialize(root.right)

    def deserialize(self, data):
        nodes = data.split(",")
        self.i = 0
        def dfs():
            if self.i == len(nodes) or nodes[self.i] == "$":
                self.i += 1
                return None
            root = TreeNode(int(nodes[self.i]))
            self.i += 1
            root.left = dfs()
            root.right = dfs()
            return root

        return dfs()
