public class Codec {

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {

        if(root==null) return "";

        List<String> data = new ArrayList<>();
        preorderTraversal(root, data);

        return String.join(",", data);
    }

    // Decodes your encoded data to tree.
    public TreeNode deserialize(String data) {

        if(data.length()==0) return null;

        String[] nodes= data.split(",");

        return convertPreorderTree( nodes, 0, nodes.length-1);
    }

    private void preorderTraversal(TreeNode root, List<String> data){

        if(root==null) return;

        data.add(""+root.val);

        preorderTraversal(root.left, data);
        preorderTraversal(root.right, data);
    }

    private TreeNode convertPreorderTree(String[] preorder, int start, int end){

        if(start > end) return null;

        TreeNode node= new TreeNode(Integer.parseInt(preorder[start]));

        int i= start+1;
        while(i<=end && Integer.parseInt(preorder[i]) < Integer.parseInt(preorder[start])){
            i++;
        }

        node.left = convertPreorderTree(preorder, start+1, i-1);
        node.right = convertPreorderTree(preorder, i, end);

        return node;
    }
}

