class Codec {
    // Encodes a tree to a single string.
    func serialize(_ root: TreeNode?) -> String {
        var arr = [String]()
        serialize(root, &arr)
        return arr.joined(separator: ",")
    }

    func serialize(_ root: TreeNode?, _ arr: inout [String]) {        
        guard let root = root else{
            return
        }
        arr.append("\(root.val)")
        serialize(root.left, &arr)
        serialize(root.right, &arr)
    }

    // Decodes your encoded data to tree.
    func deserialize(_ data: String) -> TreeNode? {
         guard data != "" else{
            return nil
        }

        let arr = data.components(separatedBy:",")
        var index = 0
        return deserialize(arr, &index, Int.min, Int.max)
    }

    func deserialize(_ arr: [String], _ index: inout Int, _ minVal: Int, _ maxVal: Int) -> TreeNode? {
        guard index < arr.count, let val = Int(arr[index]) else{
            return nil
        }

        if val < minVal || val > maxVal{
            return nil
        }

        let node = TreeNode(val)
        index += 1

        node.left = deserialize(arr, &index, minVal, val)
        node.right = deserialize(arr, &index, val, maxVal)

        return node
    }
}
