/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Codec {
public:

    //convert integer to 4 byte string
    string IntToStr( int val )
    {
        //Convert integer to char

        string s;
        char temp = (char)((val >> 24) & 0xff); //consider only last 8 bytes "->(0x01) 0x00 0x00 0x00 "
        s.push_back ( temp );

        char temp1 = (char)((val >> 16) & 0xff);
        s.push_back ( temp1 );

        char temp2 = (char)((val >> 8) & 0xff); 
        s.push_back ( temp2 );

        char temp3 = (char)((val) & 0xff); //consider only first 8 bytes "0x00 0x00 0x00 (0x01)<-"
        s.push_back ( temp3 );
       // cout << "encoded " << val << " " << s << endl;
        return s;
    }

    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {

        TreeNode* current = root;
        stack<TreeNode*> s; 
        string result = "";

        if ( !current )
            return "";


        while ( current || !s.empty() )
        {
            if ( current )
            { 
                    if ( current->right )
                    {
                        s.push ( current->right );
                    }
                    s.push ( current );
                    current = current->left;
            }else 
            {//if current null or reached the end of the tree;

                if ( s.empty() )
                    break;   

                //current's right element not yet processed;
                if ( !s.empty() ) 
                {
                    current = s.top();
                    s.pop();

                    if ( !s.empty() && current->right == s.top() )
                    {
                        s.pop();
                        s.push ( current );
                        current = current->right;
                    }
                    else 
                    {//process element;
                        result = result + IntToStr( current->val );
                        current = NULL;
                    }   
                }
            }
        }
       // cout << result << endl;
        return result;
    }


    TreeNode* deserializeHelper(vector<int>& data, int startRange, int endRange )
    {
        if ( data.size() == 0 )
            return NULL;

        int val = data.back();

       cout << "processing  " << val << endl; 
        if ( val < startRange || val >  endRange )
            return NULL;

        data.pop_back();
        cout << "taking out  " << val << endl;
        TreeNode* root = new TreeNode( val );

        root->right = deserializeHelper( data, val, endRange ); //in right value should be greater then current value
        //start = current to max int

        root->left = deserializeHelper( data, startRange, val ); //in left tree value should be less than current value; 
        //start = min int to max current val 
        return root;
    }

    //Convert 4byte string to integer 
    int StrToInt ( string s )
    {
        int result = 0;
        for ( int i = 0; i < s.size(); ++i )
        {
            //2 was encoded as "0x00 0x00 0x00 00000010"
            //256 was encoded as "0x00 0x00 000000001 0x00"
            //512 was encoded as "0x00 00000001 0x00 0x00"
            //Every char or single byte can be represented as 0-255 chars
            int temp = (int)s[i];
            result = result* 256 + temp;
        }
      //  cout << "result " << result << endl;
        return result;
    }

    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) {

         if ( data.size() == 0 )
            return NULL;

        vector<int> treeData;
        for ( int i = 0; i <= data.size() - 4; i = i + 4 ) //read 4 chars at a time
        {
            string s = data.substr(i,4);
            int temp = StrToInt(s);
            treeData.push_back ( temp );
        }

        return deserializeHelper( treeData , INT_MIN, INT_MAX ) ;
    }
};
