public class Codec
{

    // Encodes a tree to a single string.
    public string serialize(TreeNode root)
    {
        if (root == null) return string.Empty;

        StringBuilder sb = new StringBuilder();
        PreOrder(root, sb);
        sb.Remove(sb.Length - 1, 1);
        return sb.ToString();
    }

    void PreOrder(TreeNode r, StringBuilder sb)
    {
        sb.Append(r.val);
        sb.Append(",");
        if (r.left != null)
        {
            PreOrder(r.left, sb);
        }
        if (r.right != null)
        {
            PreOrder(r.right, sb);
        }
    }

    // Decodes your encoded data to tree.
    public TreeNode deserialize(string data)
    {
        if (data == string.Empty) return null;

        int[] d = data.Split(',').Select(s => int.Parse(s)).ToArray();
        int ri = 0;
        return deserialize(d, ref ri, Int32.MaxValue);
    }

    private TreeNode deserialize(int[] data, ref int ri, int max)
    {
        TreeNode r = new TreeNode(data[ri]);

        if (++ri == data.Length) return r;

        if (data[ri] <= r.val)
        {
            r.left = deserialize(data, ref ri, r.val);
        }

        if (ri == data.Length) return r;

        if (r.val < data[ri] && data[ri] <= max)
        {
            r.right = deserialize(data, ref ri, max);
        }

        return r;
    }
}

