    def serialize(self, root: TreeNode) -> str:
        q = []
        def go(root):
            if not root:
                q.append(-1)
                return
            q.append(root.val)
            go(root.left)
            go(root.right)
        go(root)
        return ' '.join([str(x) for x in q])

    def deserialize(self, data: str) -> TreeNode:
        q = deque([int(s) for s in data.split(' ')])
        def go():
            if not len(q):
                return
            x = q.popleft()
            root = TreeNode(x) if -1 < x else None
