class Codec {
public:
    using fun1 = function<void(TreeNode*)>;
    using fun2 = function<TreeNode*()>;
    string serialize(TreeNode* root, vector<int> q = {}) {
        fun1 go = [&](auto root) {
            if (!root) {
                q.push_back(-1);
                return;
            }
            q.push_back(root->val);
            go(root->left);
            go(root->right);
        };
        go(root);
