        var list = new List<string>();
        var q = new Queue<TreeNode>();
        q.Enqueue(root);
        while(q.Count>0){
            var count = q.Count;
            var node = q.Dequeue();

            if(node != null){
                list.Add(node.val.ToString());  
                q.Enqueue(node.left);
                q.Enqueue(node.right);
            }
            else{
                list.Add("#");
            }
