class Codec {
public:

    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        if(!root) return "";
        string ans = to_string(root->val) + ",";
        queue<TreeNode*>q;
        q.push(root);
        while(!q.empty()){
            root = q.front();
            q.pop();
            if(root->left){
                ans += (to_string(root->left->val) + ",");
                q.push(root->left);
            } 
            else ans += ("#,");
            if(root->right){
                ans += (to_string(root->right->val) + ",");
                q.push(root->right);
            }
            else ans += ("#,"); 
        }
        return ans;
    }

    int num(string &data,int &idx){
        string s = "";
        while(idx < data.length() && data[idx] != ','){
            s += data[idx];
            idx++;
        }
        idx++;
        if(s == "#") return -1;
        return stoi(s);
    }

    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) {
        if(data.empty()) return NULL;
        int idx = 0;
        TreeNode* root = new TreeNode(num(data,idx));
        queue<TreeNode*>q;
        q.push(root);
        while(idx < data.length() && !q.empty()){
            TreeNode* node = q.front(); q.pop();
            int left = num(data,idx);
            int right = num(data,idx);
            node->left = (left == -1) ? NULL : new TreeNode(left);
            node->right = (right == -1) ? NULL : new TreeNode(right);
            if(node->left) q.push(node->left);
            if(node->right) q.push(node->right);
        }
        return root;
    }
};

