    TreeNode* solve(vector<int>& pre,vector<int>& in,int i,int j){
            if(i > j) return NULL;
            int m;
            if(x < pre.size()){
                for(m=i;m<=j;++m){
                    if(pre[x] == in[m]){
                        x++;
                       break; 
                    }
                }
            } else return NULL;
            TreeNode* temp = new TreeNode(in[m]);
            temp->left=solve(pre,in,i,m-1);
            temp->right=solve(pre,in,m+1,j);

