public class Codec {

    void preorder(TreeNode root, List list) {
        if(root==null)
            return;
        list.add(String.valueOf(root.val));
        preorder(root.left, list);
        preorder(root.right, list);
    }

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
        List<String> list = new ArrayList<String>();
        preorder(root, list);
        String result = String.join(",",list);
        return list.size()==0?"":result;
    }

    int idx=0;
    public TreeNode decode(String[] arr, int len, int min, int max) {
        if(idx==len)
            return null;
        int val = Integer.parseInt(arr[idx]);
        if(val<min || val>max)
            return null;
        idx++;
        TreeNode node = new TreeNode(val);
        node.left = decode(arr, len, min, val);
        node.right = decode(arr, len, val, max);
        return node;
    }

    // Decodes your encoded data to tree.
    public TreeNode deserialize(String data) {
        if(data.length()==0)
            return null;
        String[] arr = data.split(",");
        return decode(arr,arr.length,Integer.MIN_VALUE,Integer.MAX_VALUE);

    }
}

