class Codec {
public:

    //use level order traversal as its easy and iterative compared to preorder
    //store level order traversal/BFS of tree and put special marker 'NULL' for NULL and ',' for separation

    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        string serializedOP="";

        queue<TreeNode*> bfs;
        bfs.push(root);

        while(!bfs.empty())
        {
            TreeNode* temp = bfs.front();
            bfs.pop();
            if(temp == NULL)
            {
                serializedOP+="NULL,";
