```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Codec {
public:

    //use level order traversal as its easy and iterative compared to preorder
    //store level order traversal/BFS of tree and put special marker 'NULL' for NULL and ',' for separation

    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        string serializedOP="";

        queue<TreeNode*> bfs;
        bfs.push(root);

        while(!bfs.empty())
        {
            TreeNode* temp = bfs.front();
            bfs.pop();
            if(temp == NULL)
            {
                serializedOP+="NULL,";
            }
            else
            {
                serializedOP+=to_string(temp->val)+",";

                bfs.push(temp->left);
                bfs.push(temp->right);
            }

        }
        //cout<<serializedOP<<endl;
        return serializedOP;
    }



    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) {
        if(data.empty())
            return NULL;
        stringstream stream(data);
        string nodeVal;
        //each getline will give new nodeVal
        std::getline(stream,nodeVal,',');

        //if first is NULL then tree is empty
        if(nodeVal == "NULL")
            return NULL;

        //create root
        TreeNode* root = new TreeNode(stoi(nodeVal));
        queue<TreeNode*> bfs; 
        bfs.push(root);


        while(!bfs.empty())
        {
            //take out first node
            TreeNode* temp = bfs.front();

            std::getline(stream,nodeVal,',');

            if(nodeVal == "NULL")
            {
                temp->left = NULL;
            }
            else
            {
                temp->left = new TreeNode(stoi(nodeVal));
                bfs.push(temp->left);
            }


            std::getline(stream,nodeVal,',');

            if(nodeVal == "NULL")
            {
                temp->right = NULL;
            }
            else
            {
                temp->right = new TreeNode(stoi(nodeVal));
                bfs.push(temp->right);
            }

            bfs.pop();
        }

        return root;
    }
};

// Your Codec object will be instantiated and called as such:
// Codec ser, deser;
// TreeNode* ans = deser.deserialize(ser.serialize(root));
