class Solution {
public:
    vector<int> findDisappearedNumbers(vector<int>& nums) {
        int n=nums.size(), loc;
        vector<int> res;
        for(int i=0; i<n; i++){
            loc = abs(nums[i])-1;
            nums[loc] = abs(nums[loc])*(-1);
        }
        for(int i=0; i<n; i++){
            if(nums[i]>0)
                res.push_back(i+1);
        }
        return res;
    }
};
