vector<int> findDisappearedNumbers(vector<int>& nums) {
	int n = nums.size();
	int i = 0;
	while (i < n) {
		if (nums[i] == i + 1) i++; //already correct position so increment i
		else {
			int correctPosn = nums[i] - 1;
			if (nums[correctPosn] == nums[i]) i++; //already present at that position
			else swap(nums, i, correctPosn); //swap
		}
	}
	vector<int> res;
	for (int i = 0; i < n; i++)
		if (nums[i] != i + 1) res.push_back(i + 1);
	return res;
}
