{
   string str="";
   fun(root,str);
   return str;          
}
void fun(TreeNode* root,string &str)
{
	if(root==NULL)
	{
		return;
	}
    str.append(to_string(root->val));
    str.push_back('-');
	fun(root->left,str);
	fun(root->right,str);

}
// Decodes your encoded data to tree.
TreeNode* deserialize(string str) 
{
	TreeNode* root=NULL;
    string s="";
    for(int i=0;i<str.length();i++)
    {
    	if(str[i]=='-')
    	{
    		int num=stoi(s);
    		root=fun(root,num);
    		s.clear();
    		continue;
		}
		else
		{
		   s.push_back(str[i]);	
		}
	}
	return root;
}
TreeNode* fun(TreeNode* root,int &num)
{
	if(root==NULL)
	{
		return new TreeNode(num);
	}
	if(num > root->val)
	{
		root->right=fun(root->right,num);
	}
	if(num < root->val)
	{
		root->left=fun(root->left,num);
	}
	return root;
}
};

