public class Codec {

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
        if(root == null) return "#";

        return root.val + "," + serialize(root.left) + "," + serialize(root.right);
    }

    // Decodes your encoded data to tree.
    public TreeNode deserialize(String data) {
        String[] values = data.split(",");

        return deserialize(values, new int[1]);
    }

    private TreeNode deserialize(String[] values, int[] pos){
        if("#".equals(values[pos[0]])){
            pos[0]++;
            return null;
        }

        TreeNode cur = new TreeNode(Integer.valueOf(values[pos[0]]));
        pos[0]++;

        cur.left = deserialize(values, pos);
        cur.right = deserialize(values, pos);

        return cur;
    }
}

