    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        string s="";
        if(!root){
            return s;
        }
        queue<TreeNode*>q;
        q.push(root);
        while(!q.empty()){
            TreeNode* node=q.front();
            q.pop();
            if(!node){
                s+="#,";
            }
            else{
                s+=to_string(node->val)+',';
                q.push(node->left);
                q.push(node->right);
            }
        }
        return s;
    }

    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) {
        if(data.empty()){return NULL;}
        string s="";
        stringstream x(data);
        getline(x, s, ',');
        TreeNode* root=new TreeNode(stoi(s));
        queue<TreeNode*>q;
        q.push(root);
        while(!q.empty()){
            TreeNode* node=q.front();
            q.pop();
            getline(x, s, ',');
            if(s=="#"){
                node->left=NULL;
            }
            else{
                TreeNode* lft=new TreeNode(stoi(s));
                node->left=lft;
                q.push(lft);
