            getline(x, s, ',');
            if(s=="#"){
                node->right=NULL;
            }
            else{
                TreeNode* rgt=new TreeNode(stoi(s));
                node->right=rgt;
                q.push(rgt);
            }
        }
        return root;
    }
};

