TreeNode* buildFromPreorderHelper(vector<int>& nums, const int start, const int end)
{
    if (start > end)
    {
        return nullptr;
    }

    int pivot;
    for (pivot = start; pivot <= end && nums[pivot] <= nums[start]; ++pivot);

	TreeNode* root = new TreeNode(nums[start]);
	root->left = buildFromPreorderHelper(nums, start + 1, pivot - 1);
    root->right = buildFromPreorderHelper(nums, pivot, end);

    return root;
}
