        if (root == null) return;
        a.append((char)(root.val));
        helper(root.left , a);
        helper(root.right , a);

    }
    // Decodes your encoded data to tree.
    int index = 0;
    public TreeNode deserialize(String data) {
        char[] input = data.toCharArray();
        return helper2(input , Integer.MIN_VALUE , Integer.MAX_VALUE);

