class Codec {
    void encode(TreeNode* root, string& data) {
        if(!root) {
            data += 'x';
            return;
        }
        data += 'n';
        char* buffer = (char*)&root->val;
        for(int i = 0; i < 4; ++i)
            data += buffer[i];
        encode(root->left, data);
        encode(root->right, data);
