public class Codec {

    // Encodes a tree to a single string.
    public String serialize(TreeNode root) {
        //pre-order root left right
        if(root == null)
            return "";
        String left = serialize(root.left);
        String right = serialize(root.right);
        return root.val + "," + left + right; 
    }

    // Decodes your encoded data to tree.
    public TreeNode deserialize(String data) {
        if(data == null || data.length() == 0)
            return null;
        String[] temp = data.split(",");
        Queue<Integer> queue = new LinkedList<>();
        for(int i = 0; i < temp.length; i ++){
            queue.offer(Integer.parseInt(temp[i]));
        }
        return build(queue, Integer.MIN_VALUE, Integer.MAX_VALUE);
    }

    private TreeNode build(Queue<Integer> queue, int low, int high){
        if(queue.isEmpty() || queue.peek() < low || queue.peek() > high){
            return null;
        }
        TreeNode root = new TreeNode(queue.poll());
        root.left = build(queue, low, root.val);
        root.right = build(queue, root.val, high);

        return root;
    }
}

