    def serialize(self, root: Optional[TreeNode]) -> str:
        """Encodes a tree to a single string.
        """
        result, q = [], []
        if root: q.append(root)
        for n in q:
            result.append(str(n.val))
            if n.left: q.append(n.left)
            if n.right: q.append(n.right)
        return ",".join(result)

    def deserialize(self, data: str) -> Optional[TreeNode]:
        """Decodes your encoded data to tree.
        """
        if len(data) == 0: return None
        nodes, q, pos = data.split(","), deque(), 1
        root = TreeNode(int(nodes[0]))
        q.append([root, -sys.maxsize, sys.maxsize])
        while q:
            n, lb, ub = q.popleft()
            if pos < len(nodes) and int(nodes[pos]) < n.val and lb <= int(nodes[pos]) <= ub:
                n.left = TreeNode(int(nodes[pos]))
                q.append([n.left, lb, n.val])
