    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        if (root == nullptr) {
            return "";
        }
        std::vector<unsigned char> bytes;
        stack<TreeNode*> st;
        st.push(root);
        while (!st.empty()) {
            TreeNode* top = st.top();
            bytes.resize(bytes.size()+sizeof(int));
            memcpy(&bytes[bytes.size()-sizeof(int)], &(top->val), sizeof(int));
            st.pop();
            if (top->left != nullptr) {
                st.push(top->left);
            }
            if (top->right != nullptr) {
                st.push(top->right);
            }
        }
        return std::string(bytes.begin(), bytes.end());

