/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */
class Codec {
public:
    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        return preorderTraverse(root);
    }

    // this function will create a preoder string of given bst
    // why preorder?
    // bcz then after we can easily create back the same bst using that preorder string of a bst
    string preorderTraverse(TreeNode* root)
    {
        if(!root)
            return"";

        return to_string(root->val) + "-" + preorderTraverse(root->left) + preorderTraverse(root->right);
    }

    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) {
        stringstream ss(data);
        string item;
        vector<int>preorder;

        // cout<<data;

        // convert string to integers again & save them to vector
        while (getline (ss, item, '-')) 
        {
            preorder.push_back(stoi(item));
        }

        int start=0,end=preorder.size()-1;
        return  makeTree(preorder,start,end);
    }

    // building out a bst from given preorder
    TreeNode* makeTree(vector<int> &preorder ,int start, int end)
    {
        if(start<=end)
        {
            TreeNode* newnode = new TreeNode(preorder[start]);

            int bigger=start+1;
            while(bigger<=end && preorder[start]>preorder[bigger])
            {
                bigger++;
            }

            if(bigger>end)
            {
                newnode->left = makeTree(preorder,start+1,end);
                newnode->right = NULL;
            }
            else
            {
                newnode->left = makeTree(preorder,start+1,bigger-1);
                newnode->right = makeTree(preorder,bigger,end);
            }

            return newnode;
        }
        else
        {
            return NULL;
        }
    }
};
