    int bs(vector<int>&v,int l,int h){
        int x=h+1;
        int i=l-1;
        while(l<=h){
            int m=(l+h)/2;
            if(v[m]>v[i]){
                x=m;
                h=m-1;
            }
            else{
                l=m+1;
            }
        }
        return x;
    }
    TreeNode* g(vector<int>&v,int l,int h){
        if(h<l)
            return NULL;
         if(l==h)
             return new TreeNode(v[l]);
        int x=bs(v,l+1,h);
