public class Codec {

    /*
    max node.val is 10000, less than 6xxxx(max value of char)   
    So just turn node.val into char.
    Serialize with preorder (parent > left > right)
    */
    public String serialize(TreeNode root) {
        StringBuilder sb = new StringBuilder();
        helper_serialize(sb, root);
        return sb.toString();
    }

    private void helper_serialize(StringBuilder sb, TreeNode root) {
        if (root == null) 
            return;
        sb.append((char)(root.val + '0'));
        helper_serialize(sb, root.left);
        helper_serialize(sb, root.right);
    }

    /*
    it's BST, so it's already in proper order as long as you add the node in order.
    */
    public TreeNode deserialize(String data) {
        TreeNode root = null;
        for(char c : data.toCharArray()) {
            root = add(root, c - '0');
        }
        return root;
    }
    private TreeNode add(TreeNode root, int val) {
        if (root == null)
            return new TreeNode(val);

        if (val < root.val) {
            root.left = add(root.left, val);
        } else {
            root.right = add(root.right, val);
        }
        return root;
    }
}
