class Codec {
public:
    int start=0;
    TreeNode* myfunc(string &S,int count){
        int temp=start;
        int count1=count;
        while(count1--){
            if(S[start]!='-'){
                start=temp;
                return NULL;
            }
            start++;
        }
        if(start>=S.size())return NULL;
        TreeNode* root=new TreeNode;
        int v=0;
        while(start<S.size()&&S[start]!='-'){
            v=v*10+S[start]-48;
            start++;
        }
        start--;
        if(v==10001){
            start++;
            return NULL;
        }
        root->val=v;
        start++;
        root->left=myfunc(S,count+1);
        root->right=myfunc(S,count+1);
        return root;
    }
    string s;
    void preorder(TreeNode* root,int level){
        if(!root)return;
        s+=to_string(root->val);
        if(root->left){
            int copy=level;
            while(copy--)s+='-';
            preorder(root->left,level+1);
        }
        else{
            int copy=level;
            while(copy--)s+='-';
            s+="10001";
        }
        if(root->right){
            int copy=level;
            while(copy--)s+='-';
            preorder(root->right,level+1);
        }
        else{
            int copy=level;
            while(copy--)s+='-';
            s+="10001";
        }
    }
    // Encodes a tree to a single string.
    string serialize(TreeNode* root) {
        preorder(root,1);
        return s;
    }

    // Decodes your encoded data to tree.
    TreeNode* deserialize(string data) {
        return myfunc(data,0);
    }
};
