public TreeNode deleteNode(TreeNode root, int key) {

        return searchAndDeleteNode (root, key);

    }

    public TreeNode searchAndDeleteNode (TreeNode root, int key) {

        // If root node is null, return null;
        if (root == null)
            return null;

        // If root's val is greater than key than search in the left tree.
        if (root.val > key) {

            root.left = searchAndDeleteNode (root.left, key);
            return root;

        // If root's val is smaller than key than search in the right tree.
        } else if (root.val < key) {

            root.right = searchAndDeleteNode (root.right, key);
            return root;

        // If root's val is the key to be deleted.
        } else {

            // If both left and right node of root is null, then simply delete this node.
            // We are in traversal of root node, we can delete it by returning null instead of root node.
            // Effectively having no more reference to this node in the tree.
            if (root.right == null && root.left == null)
                return null;

            // If there is some sub-tree or node on the left but nothing on the right, then we can return
            // root.left instead of root as root.left will be the next bigger element in the tree.
            // Or in other words root.left is the smallest element in the sub-tree of root, which deserves
            // to be put in root's place after root is removed.
            if (root.right == null) {

                return root.left;

            // If right node of root is not null.
            // We have to handle case for if left is also null and if left is not null.
            } else {

                // If left node of right is null.
                // We know root.right is the next bigger element in the sub-tree
                // of root, so we have to return this instead of returning root. (Property of BST)
                // BUT, before returning we have to take care of left node of root, so we set root.left to
                // root.right.left, which initially null.
                if (root.right.left == null) {

                    root.right.left = root.left;
                    return root.right;

                // If left node of right is not null.
                // We can get the next bigger element in the sub-tree of root by going to the extreme left
                // node from root.right.
                // Now we have delete this new possible Node from it's current reference position
                // And create a new node with this val as newRoot and refer root.left and root.right to
                // this newRoot.
                } else {

                    TreeNode current = root.right;
                    TreeNode parent = current;  // To delete the left node of parent and setting reference.

					// Keep going to the left till we find the extreme left node.
                    while (current.left != null) {

                        parent = current;
                        current = current.left;

                    }

                    // Reference to parent.left by recursively deleting this new possible node.
                    parent.left = searchAndDeleteNode (current, current.val);

                    // New Root creation with current.val.
                    TreeNode newRoot = new TreeNode (current.val);
                    newRoot.left = root.left;
                    newRoot.right = root.right;

                    return newRoot;

                }

            }

        }

    }
