    public TreeNode DeleteNode(TreeNode root, int key) {
        if (root == null) return null;
        if (root.val == key) return MergeTrees(root.right, root.left);

        if (key < root.val) root.left = DeleteNode(root.left, key);
        if (key > root.val) root.right = DeleteNode(root.right, key);
        return root;
    }

    private static TreeNode MergeTrees(TreeNode root, TreeNode node) {
        if (root == null || node == null) return root ?? node;

        if (node.val < root.val) root.left = MergeTrees(root.left, node);
        if (node.val > root.val) root.right = MergeTrees(root.right, node);
        return root;
    }
