class Solution {
    public TreeNode deleteNode(TreeNode root, int key) {
        if(root == null) return null;        
        TreeNode curr = root, parent = null;

        // 1. Search the key
        while(curr != null && curr.val != key){
            parent = curr;
            if(key < curr.val) curr = curr.left;
            else curr = curr.right;            
        }

        // 2. Perform deletion
		// When node to delete is root node
        if(parent == null) return delete(curr);

		// When node to delete is the left child of parent
		if(parent.left == curr) parent.left = delete(curr);
		// When node to delete is the right child of parent		
        else parent.right = delete(curr);

        return root;
    }

    private TreeNode delete(TreeNode nodeToDelete){
		// When there is no node-to-delete
        if(nodeToDelete == null) return null;

		// When node-to-delete has no left child then return right child
        if(nodeToDelete.left == null) return nodeToDelete.right;

		// When node-to-delete has no right child then return left child 
        if(nodeToDelete.right == null) return nodeToDelete.left;

		// When node-to-delete has both children, return the in-order successor
        TreeNode successor = nodeToDelete.right;
        TreeNode parent = null;
        while(successor.left != null){
            parent = successor;
            successor = successor.left;
        }
        // Assign left child of in-order successor to left child of node-to-delete
        successor.left = nodeToDelete.left; 

		// When in-order successor is not the immediate right child of node-to-delete, then we need to re-assign the in-order successor's right child to its parent. 
		// Also assign right child of in-order successor to right child of node-to-delete
        if (successor != nodeToDelete.right){
            parent.left = successor.right;
            successor.right = nodeToDelete.right;
        }
        return successor;
    }
}
